def get_top_crypto_symbols(n):
    top_20_cryptos = cg.get_coins_markets(vs_currency='usd', order='market_cap_desc', per_page=n)
    symbols = [crypto['symbol'].upper() + '-USD' for crypto in top_20_cryptos]
    return symbols


