cryptos = get_top_crypto_symbols(20)
tickers.extend(cryptos)


# Initialize the tables for storing the data. One for long trades, one for short trades, and one for errors.
table = PrettyTable()
table.field_names = ["Ticker", "Last price","LONG"]

table_sell = PrettyTable()
table_sell.field_names = ["Ticker", "Last price","SHORT"]

table_err = PrettyTable()
table_err.field_names = ["Ticker", "Error"]

# Loop over tickers 
with tqdm(total=len(tickers), unit="ticker") as pbar:
    for i, ticker in enumerate(tickers):
        pbar.set_description(f"Processing data for {ticker}")

        try:
            data = yf.download(ticker, period="1y", interval="1d", progress=False)
            macd = MACD(data['Close'])
            macd_line = macd.macd()
            signal_line = macd.macd_signal()

            # Get rolling maximum over a 20-day period
            macd_rolling_max = macd_line.rolling(window=20).max()
            macd_rolling_min = macd_line.rolling(window=20).min()
            higher_macd = (macd_line > macd_rolling_max.shift(1))
            lower_macd = (macd_line < macd_rolling_min.shift(1))

            price_rolling_min = data['Close'].rolling(window=20).min()
            lower_price = (data['Close'] < price_rolling_min.shift(1))

            # BUY SIGNAL
            macd_cross_up_signal = np.where((macd_line > signal_line) & (macd_line.shift(1) <= signal_line.shift(1)), 1, 0)
            macd_below_zero = np.where((macd_cross_up_signal == 1) & (macd_line < -1) , 1, 0)

            # SELL SIGNAL
            macd_cross_down_signal = np.where((macd_line < signal_line) & (macd_line.shift(1) >= signal_line.shift(1)), 1, 0)
            macd_above_zero = np.where((macd_cross_down_signal == 1) & (macd_line > 1) , 1, 0)

            last_price = round(data['Close'][-1],2)
            if any(macd_below_zero[-3:] == 1):
                table.add_row([ticker, last_price,"LONG"])

            if any(macd_above_zero[-3:] == 1):
                table_sell.add_row([ticker, last_price,"SHORT"])

            pbar.update(1)   
        except KeyError:
            table_err.add_row([ticker, "Data not found"])
        except IndexError:
            table_err.add_row([ticker, "Empty DataFrame"])
        except Exception as e:
            table_err.add_row([ticker, f"Error: {str(e)}"])
print(table)
